                e.GraphicsDeviceInformation.PresentationParameters.PresentationInterval = PresentInterval.Immediate;
            };
            gdm.SynchronizeWithVerticalRetrace = false; gdm.ApplyChanges(); //[2]
        }

        protected override void Update(GameTime gameTime) => Debug(gameTime, true); //[3]
        protected override void Draw(GameTime gameTime) => Debug(gameTime, false); //[3]

        void Debug(GameTime gameTime, bool u)
        {
            if (u) updates++;
            else draws++;
            var ifps = 1000.0 / gameTime.ElapsedGameTime.TotalMilliseconds; //[4]
            Console.WriteLine($"{(u ? 'U' : 'D')} {ifps:00.000}");
            if (updates >= stop && draws >= stop)
            {
                Console.WriteLine($"{updates} updates, {draws} draws");
                Exit();
            }
        }
    }
}
