namespace Game1
{
    public class Game1 : Game
    {
        GraphicsDeviceManager graphics;
        SpriteBatch spriteBatch;
        private Texture2D pixel;

        private int linepos = 0;

        private Rectangle screen;

        public Game1()
        {
            graphics = new GraphicsDeviceManager(this);
            graphics.IsFullScreen = true;
            graphics.PreferredBackBufferWidth = 1920;
            graphics.PreferredBackBufferHeight = 1080;
            Content.RootDirectory = "Content";
        }
        protected override void Initialize()
        {
            graphics.IsFullScreen = true;

            this.IsFixedTimeStep = true;
            this.TargetElapsedTime = new System.TimeSpan(166666L);
            screen = GraphicsDevice.Adapter.CurrentDisplayMode.TitleSafeArea;

            base.Initialize();
        }
        protected override void LoadContent()
        {
            spriteBatch = new SpriteBatch(GraphicsDevice);
            pixel = Content.Load<Texture2D>("pixel");
        }
        protected override void UnloadContent()
        {
        }

        protected override void Update(GameTime gameTime)
        {
            if (GamePad.GetState(PlayerIndex.One).Buttons.Back == ButtonState.Pressed || Keyboard.GetState().IsKeyDown(Keys.Escape))
                Exit();

            linepos = (linepos + 5) % screen.Height;

            base.Update(gameTime);
        }

        protected override void Draw(GameTime gameTime)
        {
            GraphicsDevice.Clear(Color.White);
            spriteBatch.Begin(samplerState: SamplerState.LinearWrap);
            spriteBatch.Draw(pixel, new Rectangle(0, linepos, screen.Width, 5), new Rectangle(0, 0, screen.Width, 5), Color.White);
            spriteBatch.End();
            //Thread.Sleep( (int)gameTime.ElapsedGameTime.TotalMilliseconds/8);

            base.Draw(gameTime);
        }
    }
}
