namespace DTBug {
    /// <summary>
    /// This is the main type for your game
    /// </summary>
    public class Game1 : Game {

        GraphicsDeviceManager graphics;
        SpriteBatch spriteBatch;
        Texture2D texture_0, texture_1;
        RenderTarget2D rt;

        VertexBuffer v_buffer;
        DualTextureEffect dt_effect;

        public struct VertexDualTexture : IVertexType {

            Vector3 position;
            Vector2 tex1, tex2;

            public Vector3 Position { get { return position; } set { position = value; } }
            public Vector2 TextureCoordinate { get { return tex1; } set { tex1 = value; } }
            public Vector2 TextureCoordinate2 { get { return tex2; } set { tex2 = value; } }

            public readonly static VertexDeclaration dec = new VertexDeclaration
            (
                new VertexElement(0, VertexElementFormat.Vector3, VertexElementUsage.Position, 0),
                new VertexElement(12, VertexElementFormat.Vector2, VertexElementUsage.TextureCoordinate, 0),
                new VertexElement(20, VertexElementFormat.Vector2, VertexElementUsage.TextureCoordinate, 1)
            );

            VertexDeclaration IVertexType.VertexDeclaration { get { return dec; } }
        }


        VertexDualTexture[] quad = new[] {
            new VertexDualTexture() { Position = new Vector3(120, 120 + 256, 0), TextureCoordinate = new Vector2(0, 1), TextureCoordinate2 = new Vector2(0, 1) },
            new VertexDualTexture() { Position = new Vector3(120, 120, 0), TextureCoordinate = new Vector2(0, 0), TextureCoordinate2 = new Vector2(0, 0) },
            new VertexDualTexture() { Position = new Vector3(120 + 256, 120 + 256, 0), TextureCoordinate = new Vector2(1, 1), TextureCoordinate2 = new Vector2(1, 1) },
            new VertexDualTexture() { Position = new Vector3(120 + 256, 120, 0), TextureCoordinate = new Vector2(1, 0), TextureCoordinate2 = new Vector2(1, 0) }
        };

