using System;
using System.IO;
using System.Runtime.Serialization.Formatters.Binary;

namespace ConsoleApplicationVS2017
{
    public static class Serialization
    {
        public static byte[] Serialize(Func<int> o)
        {
            var binaryFormatter = new BinaryFormatter();
            using (var stream = new MemoryStream())
            {
                binaryFormatter.Serialize(stream, o);
                return stream.ToArray();
            }
        }
    }

    [Serializable]
    public class Class1
    {
        private int Property { get; set; }

        public void Run()
        {
            Serialization.Serialize(() => Property + 5);

            var localVariable = Property;
            Func<int, int> func2 = o => localVariable + o;
        }
    }

    public static class Program
    {
        public static void Main()
        {
            new Class1().Run();
        }
    }
}

